    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {// GEN-FIRST:event_jButton2ActionPerformed

        String valorTXTTemp = textoGrados.getText();
        double valorTemp = Double.parseDouble(valorTXTTemp);

        int tempInit = selecUnGrados.getSelectedIndex();
        int tempFin = selecUnConv.getSelectedIndex();

        Intercambio intercambioTemp = new Intercambio();
        intercambioTemp.setValorTemp(valorTemp);
        intercambioTemp.setTempInit(tempInit);
        intercambioTemp.setTempFin(tempFin);

        double resultFinalTemp = intercambioTemp.convertirTemp();
        String resultFinalTempString = String.valueOf(resultFinalTemp);

        textoConvTemp.setText(resultFinalTempString);
    }// GEN-LAST:event_jButton2ActionPerformed

